import React, { useState, useEffect } from "react";
import axios from "axios";

function QuestionForm() {
    const [questions, setQuestions] = useState([]);

    useEffect(() => {
        // Adjust the URL below according to your API
        axios.get("http://localhost:5162/api/Users/Questions")
            .then(response => {
                setQuestions(response.data);
            });
    }, []);

    return (
        <div>
            {questions.map(question => (
                <div key={question.id}>
                    <h2>{question.questionText}</h2>
                    {question.answers.map(answer => (
                        <div key={answer.id}>
                            <input type="radio" id={answer.id} name={question.id} value={answer.scoreValue} />
                            <label htmlFor={answer.id}>{answer.answerText}</label>
                        </div>
                    ))}
                </div>
            ))}
        </div>
    );
}

export default QuestionForm;
