import React, { useState } from 'react';
import styles from '../Css/ReactionForm.module.css';
import ReactionForm from './ReactionForm';

const IntroPage = () => {
  const [isIntroRead, setIsIntroRead] = useState(false);

  const handleIntroRead = () => {
    setIsIntroRead(true);
  };

  return (
    <div className={styles["form-control"]}>
      {!isIntroRead ? (
        <div>
          <h1>Welcome!</h1>
          <p>This is the introduction text that explains the form to the user. 
          Please read carefully before proceeding to the question form.</p>
          <button className={styles["button"]} onClick={handleIntroRead}>Go to question form</button>
        </div>
      ) : (
        <ReactionForm />
      )}
    </div>
  );
};

export default IntroPage;
