import React, { useState, useEffect } from "react";
import axios from "axios";
import { useNavigate } from 'react-router-dom';

function QuestionForm({ loggedInUserId }) {
    const [questions, setQuestions] = useState([]);
    const [answers, setAnswers] = useState({});
    const navigate = useNavigate();

    useEffect(() => {
        const token = localStorage.getItem('token');
      
        axios.get("http://localhost:5162/api/Questions", {
            headers: {
                Authorization: `Bearer ${token}`
            }
        })
        .then(response => {
            setQuestions(response.data);
        })
        .catch(error => {
            // Handle error
        });
    }, []);
      

    const handleAnswerChange = (questionId, scoreValue) => {
        setAnswers(prevAnswers => ({
            ...prevAnswers,
            [questionId]: parseInt(scoreValue)
        }));
    };

    const handleSubmit = () => {
        const totalScoreValue = Object.values(answers).reduce((a, b) => a + b, 0);
        const totalScore = {
            TotalScoreValue: totalScoreValue,
            UserId: loggedInUserId // Assign the logged-in user ID
        };

        const token = localStorage.getItem('token');

        axios.post("http://localhost:5162/api/TotalScores", totalScore, {
            headers: {
                'Authorization': `Bearer ${token}`
            }
        })
        .then(response => {
            navigate('/dashboard');
        })
        .catch(error => {
            // Handle the error if needed
        });
    };

    return (
        <div>
            <h1>Questions</h1>
            {questions.map(question => (
                <div key={question.id}>
                    <h2>{question.questionText}</h2>
                    {question.answers.map(answer => (
                        <div key={answer.id}>
                            <input 
                                type="radio" 
                                id={answer.id} 
                                name={question.id} 
                                value={answer.id} 
                                onChange={() => handleAnswerChange(question.id, answer.scoreValue)}
                            />
                            <label htmlFor={answer.id}>{answer.answerText}</label>
                        </div>
                    ))}
                </div>
            ))}
            <button onClick={handleSubmit}>Submit</button>
        </div>
    );
}

export default QuestionForm;
