import React, { useState } from "react";
import axios from "axios";
import AdminSidebar from './AdminSidebar';
import AdminHeader from './AdminHeader';
import styles from '../../Css/AddCompany.module.css';
import config from '../../config';

function AddCompany() {
  const [company, setCompany] = useState({
    name: "",
    description: "",
    code: Math.random().toString(36).substr(2, 10)
  });

  const [isCompanyAdded, setIsCompanyAdded] = useState(false);

  const handleInputChange = (e) => {
    setCompany({
      ...company,
      [e.target.name]: e.target.value
    });
  };

  const handleNewCompanySubmit = async (e) => {
    e.preventDefault();

    const formData = new FormData();
    formData.append('name', company.name);
    formData.append('description', company.description);
    formData.append('code', company.code);

    try {
      const token = localStorage.getItem('token');
      await axios.post(`${config.API_BASE_URL}/api/Companies`, formData, {
        headers: {
          Authorization: `Bearer ${token}`,
          'Content-Type': 'application/json'
        }
      });
      setIsCompanyAdded(true);
      setCompany({
        name: "",
        description: "",
        code: Math.random().toString(36).substr(2, 10)
      });
    } catch (error) {
      console.error(error);
    }
  };

  return (

      <div className={styles.dashboard}>
      <AdminHeader />
        <div className={styles.sidebar}>
          <AdminSidebar />
        </div>
        <div className={styles.content}>
          <h1>Add New Company</h1>
          {isCompanyAdded && <p>Company added successfully!</p>}
          <form onSubmit={handleNewCompanySubmit}>
            <label>
              Name:
              <input type="text" name="name" value={company.name} onChange={handleInputChange} required />
            </label>
            <label>
              Description:
              <input type="text" name="description" value={company.description} onChange={handleInputChange} />
            </label>
            <button type="submit">Add Company</button>
          </form>
        </div>
      </div>

  );
}

export default AddCompany;
