import React, { useEffect, useState } from 'react';
import axios from 'axios';
import config from '../../config';

function EditOnderwerp({ match }) {
    const [onderwerp, setOnderwerp] = useState({
        id: 0,
        name: "",
        description: ""
    });

    const fetchOnderwerp = async () => {
        try {
            const response = await axios.get(`${config.API_BASE_URL}/api/Onderwerp/${match.params.id}`, {
                headers: {
                    'Content-Type': 'application/json',
                    Authorization: `Bearer ${localStorage.getItem('token')}`
                }
            });
            setOnderwerp(response.data);
        } catch (error) {
            console.error(error);
        }
    };

    useEffect(() => {
        fetchOnderwerp();
    }, []);

    const handleInputChange = (e) => {
        setOnderwerp({
            ...onderwerp,
            [e.target.name]: e.target.value
        });
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            await axios.put(`${config.API_BASE_URL}/api/Onderwerp/${onderwerp.id}`, onderwerp, {
                headers: {
                    'Content-Type': 'application/json',
                    Authorization: `Bearer ${localStorage.getItem('token')}`
                }
            });
            alert('Onderwerp updated successfully!');
        } catch (error) {
            console.error(error);
        }
    };

    return (
        <div>
            <h2>Edit Onderwerp</h2>
            <form onSubmit={handleSubmit}>
                <label>
                    Name:
                    <input type="text" name="name" value={onderwerp.name} onChange={handleInputChange} required />
                </label>
                <label>
                    Description:
                    <input type="text" name="description" value={onderwerp.description} onChange={handleInputChange} required />
                </label>
                <button type="submit">Update Onderwerp</button>
            </form>
        </div>
    );
}

export default EditOnderwerp;
