import React, { Component } from 'react';
import axios from 'axios';
import config from '../config';
import styles from '../Css/CompanyDashboard.module.css';
import DashboardSidebar from './DashboardSidebar';
import DashboardHeader from './DashboardHeader';

const descriptions = [
  "Analyticus (C)", "Strateeg (Cd)", "Perfectionist (Cs)", "Raadgever (Ci)",
  "Pionier (Dc)", "Beslisser (D)", "Doorzetter (Ds)", "Avonturier (Di)",
  "Specialist (Sc)", "Doener (Sd)", "Dienstverlener (S)", "Helper (Si)",
  "Diplomaat (Ic)", "Inspirator (Id)", "Bemiddelaar (Is)", "Entertainer (I)"
];

const boxCodeToIndex = {
  "C": 0, "D": 1, "S": 2, "I": 3,
  "c": 0, "d": 1, "s": 2, "i": 3,
};

class CompanyDashboard extends Component {
  state = {
    userBoxes: [],
    userScores: [],
    companyId: null
  };

  componentDidMount() {
    this.fetchUserBoxes();
    this.fetchUserScores();
  }

  fetchUserBoxes = async () => {
    try {
      const token = localStorage.getItem('token');
      const localConfig = {
        headers: { Authorization: `Bearer ${token}` }
      };
      
      // Fetch the profile data
      const profileResponse = await axios.get(`${config.API_BASE_URL}/api/Users/Profile`, localConfig);
      

      const response = await axios.get(`${config.API_BASE_URL}/api/Companies/${profileResponse.data.companyId}/users`);
      const data = response.data;
      // Ensure data is an array before setting it
      this.setState({ userBoxes: Array.isArray(data) ? data : [] });
      
    } catch (error) {
      console.error('Error fetching user boxes:', error);
    }
  };

  fetchUserScores = async () => {
    try {
      const response = await axios.get(`${config.API_BASE_URL}/api/TotalScores/all`);
      const data = response.data;
      // Ensure data is an array before setting it
      this.setState({ userScores: Array.isArray(data) ? data : [] });
      console.log(data);
    } catch (error) {
      console.error('Error fetching user scores:', error);
    }
  };

  render() {
    const { userBoxes, userScores } = this.state;

    return (
      <div className={styles.dashboard}>
        <DashboardSidebar />
        <div className={styles.main}>
          <DashboardHeader />
          <div className={styles.content}>
            <div className={styles["dashboard-title"]}>
              <h1>Company Dashboard</h1>
              <p>Welcome to the Company Dashboard page!</p>
            </div>

            <div className={styles["dashboard-content"]}>
              {/* Rest of the component */}
            </div>
          </div>
        </div>
      </div>
    );
  }
}

export default CompanyDashboard;
