import React, { useState, useEffect } from "react";
import axios from "axios";
import { useNavigate } from 'react-router-dom';

function QuestionForm() {
    const [questions, setQuestions] = useState([]);
    const [answers, setAnswers] = useState({});
    const [loggedInUserId, setLoggedInUserId] = useState(null);
    const [loading, setLoading] = useState(true);
    const navigate = useNavigate();

    useEffect(() => {
        const fetchData = async () => {
            const token = localStorage.getItem('token');
            const questionsResponse = await axios.get("http://localhost:5162/api/Questions", {
                headers: {
                    Authorization: `Bearer ${token}`
                }
            });
            const totalScoresResponse = await axios.get("http://localhost:5162/api/TotalScores/user", {
                headers: {
                    Authorization: `Bearer ${token}`
                }
            });
            setQuestions(questionsResponse.data);
            setLoggedInUserId(totalScoresResponse.data.userId);
            setLoading(false);
        };

        fetchData().catch(error => {
            // Handle error
            console.error(error);
            setLoading(false);
        });

    }, []);
      
    const handleAnswerChange = (questionId, scoreValue) => {
        setAnswers(prevAnswers => ({
            ...prevAnswers,
            [questionId]: parseInt(scoreValue)
        }));
    };

    const handleSubmit = () => {
        const totalScoreValue = Object.values(answers).reduce((a, b) => a + b, 0);
        const totalScore = {
            userId: loggedInUserId,
            score: totalScoreValue
        };
    
        const token = localStorage.getItem('token');
        axios.post("http://localhost:5162/api/TotalScores", totalScore, {
            headers: {
                'Authorization': `Bearer ${token}`
            }
        })
        .then(response => {
            navigate('/dashboard');
        })
        .catch(error => {
            // Handle the error if needed
        });
    };

    if (loading) {
        return <div>Loading...</div>;
    }

    return (
        <div>
            <h1>Questions</h1>
            {questions.map(question => (
                <div key={question.id}>
                    <h2>{question.questionText}</h2>
                    {question.answers.map(answer => (
                        <div key={answer.id}>
                            <input 
                                type="radio" 
                                id={answer.id} 
                                name={question.id} 
                                value={answer.id} 
                                onChange={() => handleAnswerChange(question.id, answer.scoreValue)}
                            />
                            <label htmlFor={answer.id}>{answer.answerText}</label>
                        </div>
                    ))}
                </div>
            ))}
            <button onClick={handleSubmit}>Submit</button>
        </div>
    );
}

export default QuestionForm;
