import React, { useEffect, useState } from 'react';
import Chart from 'react-apexcharts';
import DashboardSidebar from './DashboardSidebar';
import styles from '../Css/Dashboard.module.css';
import axios from 'axios';
import config from '../config';

const Header = ({ title }) => (
  <div className={styles.header}>
    <hr />
    <div className={styles["page-title"]}>{title}</div>
  </div>
);

const Dashboard = () => {
  const [chartOptions, setChartOptions] = useState({
    chart: {
      height: 600,
      type: 'radar',
      width: '100%', // This can be a percentage or a pixel value
    },
    xaxis: {
      categories: ['Dominant', 'Invloedrijk', 'Stabiel', 'Consciëntieus']
    }
  });
  const [chartSeries, setChartSeries] = useState([]);
  const [resultData, setResultData] = useState(null);
  const [onderwerpData, setOnderwerpData] = useState(null);

  useEffect(() => {
    const token = localStorage.getItem('token');

    fetch(`${config.API_BASE_URL}/api/TotalScores/user/me`, {
      headers: {
        'Authorization': `Bearer ${token}`,
      },
    })
    .then(response => {
      if (response.ok) {
        return response.json();
      } else {
        throw new Error('Network response was not ok');
      }
    })
    .then(serverData => {
      setResultData(serverData);

      setChartSeries([
        {
          name: 'Score',
          data: [serverData.scoreValueD, serverData.scoreValueI, serverData.scoreValueS, serverData.scoreValueC]
        }
      ]);

      axios.get(`${config.API_BASE_URL}/api/Onderwerp/user/${serverData.userId}`, {
        headers: {
          'Authorization': `Bearer ${token}`,
        },
      })
      .then(response => {
        setOnderwerpData(response.data);
      })
      .catch(error => {
        console.error('There has been a problem with your fetch operation:', error);
      });
    })
    .catch(error => {
      console.error('There has been a problem with your fetch operation:', error);
    });
  }, []);

  return (
    <div className={styles.dashboard}>
      <DashboardSidebar />
      <div className={styles.main}>
        <Header title={onderwerpData ? onderwerpData.name : 'Loading...'} />
        <div className={styles.content}>
          <div className={styles["chart-container"]}>
            <div className={styles["radar-chart"]}>
              <Chart options={chartOptions} className={styles["chart-container"]} series={chartSeries} type="radar" height={350} />
            </div>
            {resultData && (
              <div className={styles.results}>
                <div>D: {resultData.scoreValueD}</div>
                <div>I: {resultData.scoreValueI}</div>
                <div>S: {resultData.scoreValueS}</div>
                <div>C: {resultData.scoreValueC}</div>
              </div>
            )}
          </div>
          <div className={styles["text-content"]}>
            <h2>{onderwerpData ? onderwerpData.name : 'Loading...'}</h2>
            <p>{onderwerpData ? onderwerpData.description : 'Loading...'}</p>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Dashboard;
