import React, { useState, useEffect } from 'react';
import config from '../../config';
import DashboardSidebar from './AdminSidebar';
import styles from '../../Css/Dashboard.module.css';

const Header = ({ title }) => (
  <div className={styles.header}>
    <hr />
    <div className={styles["page-title"]}>{title}</div>
  </div>
);

function AdminEditQuestions() {
  const [questions, setQuestions] = useState([]);
  const [companies, setCompanies] = useState([]);
  const [selectedCompany, setSelectedCompany] = useState('');

  useEffect(() => {
    fetch(`${config.API_BASE_URL}/api/companies`)
      .then(response => response.json())
      .then(data => setCompanies(data));
  }, []);

  const handleCompanyChange = (event) => {
    const companyId = event.target.value;
    setSelectedCompany(companyId);

    if (companyId) {
      fetch(`${config.API_BASE_URL}/api/Questions/${companyId}`, {
        headers: {
          Authorization: `Bearer ${localStorage.getItem('token')}`
        }
      })
        .then(response => response.json())
        .then(data => setQuestions(data))
        .catch(error => console.error(error));
    }
  };

  return (
    <div className={styles.dashboard}>
      <DashboardSidebar />
      <div className={styles.main}>
        <Header title="Admin Edit Questions" />
        <select value={selectedCompany} onChange={handleCompanyChange}>
          <option value="">Select a company...</option>
          {companies.map(company => (
            <option key={company.id} value={company.id}>{company.name}</option>
          ))}
        </select>

        {questions.length > 0 ? (
          <div>
            <h2>Existing Questions for Company: {selectedCompany}</h2>
            <ul>
              {questions.map(question => (
                <li key={question.id}>
                  <h3>{question.questionText}</h3>
                  <ul>
                    {question.answers.map(answer => (
                      <li key={answer.id}>
                        <p>Answer Text: {answer.answerText}</p>
                        <p>Score Value D: {answer.scoreValueD}</p>
                        <p>Score Value I: {answer.scoreValueI}</p>
                        <p>Score Value S: {answer.scoreValueS}</p>
                        <p>Score Value C: {answer.scoreValueC}</p>
                      </li>
                    ))}
                  </ul>
                </li>
              ))}
            </ul>
          </div>
        ) : (
          <p>No questions found for the selected company.</p>
        )}

        <h2>Add New Question</h2>
        {/* Add your code for adding a new question */}
      </div>
    </div>
  );
}

export default AdminEditQuestions;
