// EditOnderwerp.js
import React, { useState, useEffect } from "react";
import axios from "axios";
import AdminSidebar from './AdminSidebar';
import AdminHeader from './AdminHeader';
import styles from '../../Css/AddCompany.module.css';  // you might want to create separate css module for this component
import config from '../../config';

function EditOnderwerp() {
  const [onderwerpen, setOnderwerpen] = useState([]);
  const [selectedOnderwerp, setSelectedOnderwerp] = useState("");
  const [onderwerpDetails, setOnderwerpDetails] = useState({ name: "", description: "" });
  const [isOnderwerpUpdated, setIsOnderwerpUpdated] = useState(false);

  useEffect(() => {
    fetchOnderwerpen();
  }, []);

  const fetchOnderwerpen = async () => {
    const token = localStorage.getItem('token');
    const response = await axios.get(`${config.API_BASE_URL}/api/Onderwerp`, {
      headers: {
        Authorization: `Bearer ${token}`
      }
    });
    setOnderwerpen(response.data);
  };

  const handleOnderwerpChange = async (e) => {
    setSelectedOnderwerp(e.target.value);
    const token = localStorage.getItem('token');
    const response = await axios.get(`${config.API_BASE_URL}/api/Onderwerp/${e.target.value}`, {
      headers: {
        Authorization: `Bearer ${token}`
      }
    });
    setOnderwerpDetails({ name: response.data.name, description: response.data.description });
  };

  const handleInputChange = (e) => {
    setOnderwerpDetails({
      ...onderwerpDetails,
      [e.target.name]: e.target.value
    });
  };

  const handleUpdateOnderwerp = async (e) => {
    e.preventDefault();

    const token = localStorage.getItem('token');
    await axios.put(`${config.API_BASE_URL}/api/Onderwerp/${selectedOnderwerp}`, onderwerpDetails, {
      headers: {
        Authorization: `Bearer ${token}`
      }
    });
    setIsOnderwerpUpdated(true);
    fetchOnderwerpen();
  };

  return (
    <>
      <AdminHeader />
      <div className={styles.container}>
        <div className={styles.sidebar}>
          <AdminSidebar />
        </div>
        <div className={styles.content}>
          <h1>Edit Onderwerp</h1>
          {isOnderwerpUpdated && <p>Onderwerp updated successfully!</p>}
          <form onSubmit={handleUpdateOnderwerp}>
            <label>
              Onderwerp:
              <select name="onderwerpId" value={selectedOnderwerp} onChange={handleOnderwerpChange} required>
                <option value="">Select onderwerp</option>
                {onderwerpen && onderwerpen.map((onderwerp) => (
                  <option key={onderwerp.id} value={onderwerp.id}>
                    {onderwerp.name}
                  </option>
                ))}
              </select>
            </label>
            <label>
              Name:
              <input type="text" name="name" value={onderwerpDetails.name} onChange={handleInputChange} required />
            </label>
            <label>
              Description:
              <input type="text" name="description" value={onderwerpDetails.description} onChange={handleInputChange} required />
            </label>
            <button type="submit">Update Onderwerp</button>
          </form>
        </div>
      </div>
    </>
  );
}

export default EditOnderwerp;
