import React, { useState, useEffect } from "react";
import axios from "axios";
import AdminSidebar from './AdminSidebar';
import AdminHeader from './AdminHeader';
import styles from '../../Css/ResetPassword.module.css';
import config from '../../config';

function ResetPassword() {
  const [selectedUser, setSelectedUser] = useState("");
  const [users, setUsers] = useState([]);
  const [generatedToken, setGeneratedToken] = useState("");

  useEffect(() => {
    const fetchUsers = async () => {
      const token = localStorage.getItem('token');
      const response = await axios.get(`${config.API_BASE_URL}/api/Users`, {
        headers: {
          Authorization: `Bearer ${token}`
        }
      });

      setUsers(response.data);
    };

    fetchUsers();
  }, []);

  const handleGenerateResetToken = async () => {
    const token = localStorage.getItem('token');
    const response = await axios.post(`${config.API_BASE_URL}/api/Users/GenerateResetToken/${selectedUser}`, {}, {
      headers: {
        Authorization: `Bearer ${token}`
      }
    });

    setGeneratedToken(response.data.token);
  };

  return (
    <>
      <AdminHeader />
      <div className={styles.container}>
        <div className={styles.sidebar}>
          <AdminSidebar />
        </div>
        <div className={styles.content}>
          <h1>Generate Password Reset Token</h1>
          <div>
            <label>
              Select User:
              <select name="selectedUser" value={selectedUser} onChange={(e) => setSelectedUser(e.target.value)}>
                {users.map((user) => (
                  <option key={user.id} value={user.id}>
                    {user.username}
                  </option>
                ))}
              </select>
            </label>
            <button onClick={handleGenerateResetToken}>Generate Token</button>
          </div>
          {generatedToken && <p>The generated token is: {generatedToken}</p>}
        </div>
      </div>
    </>
  );
}

export default ResetPassword;
