import React from 'react';
import axios from 'axios';

class QuestionForm extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      questions: [],
      answers: {},
    };
  }

  async componentDidMount() {
    try {
      const result = await axios.get("http://localhost:5162/api/Questions");
      this.setState({ questions: result.data });
    } catch (error) {
      console.error('Failed to fetch questions:', error);
    }
  }

  handleAnswerChange = (questionId, answerId) => {
    this.setState(prevState => ({
      answers: {
        ...prevState.answers,
        [questionId]: answerId
      }
    }));
  };

  handleSubmit = (event) => {
    event.preventDefault();
    // Do something with this.state.answers here
  };

  render() {
    const { questions, answers } = this.state;
    return (
      <div>
        {questions.length > 0 ? (
          <form onSubmit={this.handleSubmit}>
            {questions.map((question) => (
              <div key={question.id}>
                <h3>{question.questionText}</h3>
                {question.answers.map((answer) => (
                  <div key={answer.id}>
                    <input
                      type="radio"
                      id={answer.id.toString()}
                      name={question.id.toString()}
                      value={answer.id}
                      checked={answers[question.id] === answer.id}
                      onChange={() => this.handleAnswerChange(question.id, answer.id)}
                    />
                    <label htmlFor={answer.id.toString()}>{answer.answerText}</label>
                  </div>
                ))}
              </div>
            ))}
            <button type="submit">Submit</button>
          </form>
        ) : (
          <div>No questions loaded</div>
        )}
      </div>
    );
  }
}

export default QuestionForm;
