import React, { useEffect, useState } from 'react';
import { Radar } from 'primereact/chart';
import DashboardSidebar from './DashboardSidebar';
import styles from '../Css/Dashboard.module.css';
import axios from 'axios';
import config from '../config';

const Header = ({ title }) => (
  <div className={styles.header}>
    <hr />
    <div className={styles["page-title"]}>{title}</div>
  </div>
);

const Dashboard = () => {
  const [chartData, setChartData] = useState([]);
  const [resultData, setResultData] = useState(null);
  const [onderwerpData, setOnderwerpData] = useState(null);

  useEffect(() => {
    const token = localStorage.getItem('token');

    // Same fetch and axios code here...
  }, []);

  // This options should be changed according to your needs
  const chartOptions = {
    scale: {
      ticks: {
        min: 0,
        max: 40,
      },
      pointLabels: {
        fontSize: 16,
        fontWeight: 'bold',
      },
    },
    legend: {
      display: false,
    },
  };

  const radarData = {
    labels: chartData.map(data => data.subject),
    datasets: [
      {
        label: 'Score',
        backgroundColor: 'rgba(52, 135, 199, 0.6)',
        borderColor: 'rgba(52, 135, 199, 1)',
        pointBackgroundColor: 'rgba(52, 135, 199, 1)',
        data: chartData.map(data => data.A),
      },
    ],
  };

  return (
    <div className={styles.dashboard}>
      <DashboardSidebar />
      <div className={styles.main}>
        <Header title={onderwerpData ? onderwerpData.name : 'Loading...'} />
        <div className={styles.content}>
          <div className={styles["chart-container"]}>
            <div className={styles["radar-chart"]}>
              <Radar data={radarData} options={chartOptions} />
            </div>
            {/* Rest of your component code here... */}
          </div>
        </div>
      </div>
    </div>
  );
};

export default Dashboard;
