import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import styles from '../Css/ReactionForm.module.css';
import config from '../config';
import * as Survey from "survey-react";
import "survey-react/survey.css";

Survey.StylesManager.applyTheme("default");

const ReactionForm = () => {
  const [questions, setQuestions] = useState([]);
  const [userId, setUserId] = useState(null);
  const navigate = useNavigate();

  useEffect(() => {
    const fetchProfileAndQuestions = async () => {
      try {
        const token = localStorage.getItem('token');
        const localConfig = {
          headers: { Authorization: `Bearer ${token}` }
        };
        
        const profileResponse = await axios.get(`${config.API_BASE_URL}/api/Users/Profile`, localConfig);
        const questionsResponse = await axios.get(`${config.API_BASE_URL}/api/ReactionForm?companyId=${profileResponse.data.companyId}`, localConfig);

        setUserId(profileResponse.data.id);

        // Update setQuestions to transform the fetched data into SurveyJS format
        const surveyQuestions = questionsResponse.data.map((question, index) => ({
          type: "radiogroup",
          name: `question${index}`,
          title: question.questionText,
          choices: question.answers.map((answer) => ({
            value: answer.id,
            text: answer.answerText,
          })),
        }));

        setQuestions(surveyQuestions);
      } catch (error) {
        console.error('Failed to fetch profile or questions:', error);
      }
    };
    fetchProfileAndQuestions();
  }, []);

  const handleSubmit = async (survey) => {
    // Access survey data via survey.data
    const { data } = survey;

    // Calculate total score
    let totalScoreD = 0;
    let totalScoreI = 0;
    let totalScoreS = 0;
    let totalScoreC = 0;

    // Loop over the answers
    for (const key in data) {
      // Get the questionId from the key and the selected answerId from the value
      const questionId = parseInt(key.replace("question", ""), 10);
      const answerId = data[key];

      // Find the corresponding question and answer
      const question = questions[questionId];
      const selectedAnswer = question.answers.find((answer) => answer.id === answerId);

      if (selectedAnswer) {
        totalScoreD += selectedAnswer.scoreValueD;
        totalScoreI += selectedAnswer.scoreValueI;
        totalScoreS += selectedAnswer.scoreValueS;
        totalScoreC += selectedAnswer.scoreValueC;
      }
    }

    const totalScore = {
      scoreValueD: totalScoreD,
      scoreValueI: totalScoreI,
      scoreValueS: totalScoreS,
      scoreValueC: totalScoreC,
      userId: userId,
    };

    // Submit total score
    try {
      const token = localStorage.getItem('token');
      const localConfig = {
        headers: { Authorization: `Bearer ${token}` },
      };
      const response = await axios.post(
        `${config.API_BASE_URL}/api/TotalScores`,
        totalScore,
        localConfig
      );
      console.log(response.data);
      navigate('/dashboard');
    } catch (error) {
      console.error("Failed to submit total score:", error);
    }
  };

  return (
    <div className={styles["form-control"]}>
      {questions.length > 0 ? (
        <Survey.Survey 
          showCompletedPage={false}
          onComplete={handleSubmit}
          json={{ questions }}
        />
      ) : (
        <p>There are no questions</p>
      )}
    </div>
  );
};

export default ReactionForm;
